import axios from "axios";
import { CompanySearch } from "./company";



    interface SearchResponse {
        data : CompanySearch[];
    }

export const searchCompanies = async (query: string) => {
  try {
    const [symbolRes, nameRes] = await Promise.all([
      axios.get<SearchResponse>(
        `https://financialmodelingprep.com/stable/search-symbol?query=${query}&apikey=${process.env.REACT_APP_API_KEY}`
      ),
      axios.get<SearchResponse>(
        `https://financialmodelingprep.com/stable/search-name?query=${query}&apikey=${process.env.REACT_APP_API_KEY}`
      ),
    ]);

    // both return arrays → merge
    const data = [symbolRes.data, nameRes.data];

    // if you want to remove duplicates by symbol:


    return data;
  } catch (error) {
    if (axios.isAxiosError(error)) {
      console.log("Error message: ", error.message);
      return error.message;
    } else {
      console.log("Unexpected error: ", error);
      return "An unexpected error occurred";
    }
  }
};
