import React, { JSX } from 'react'
import './Card.css'
import { CompanySearch } from '../../company';
import AddPortfolio from '../Portfolio/AddPortfolio/AddPortfolio';
import { on } from 'events';
import { Link } from 'react-router-dom';

interface Props {
  id:string;
  searchResult: CompanySearch;
  onPortfolioCreate: (e: React.SyntheticEvent) => void;
}

const Card: React.FC<Props> = ({id, searchResult, onPortfolioCreate}: Props) : JSX.Element => {
  return (
  <div
      className="flex flex-col items-center justify-between w-full p-5 bg-slate-100 rounded-lg md:flex-row"
      key={id}
      id={id}
    >
      <Link to = {`/company/${searchResult.symbol}/company-profile`} className="font-bold text-center text-black md:text-left">
        {searchResult.name} ({searchResult.symbol})
      </Link>
      <p className="text-black">{searchResult.currency}</p>
      <p className="font-bold text-black">
        {searchResult.exchangeShortName} - {searchResult.stockExchange}
      </p>
      <AddPortfolio
        onPortfolioCreate={onPortfolioCreate}
        symbol={searchResult.symbol}
      />
    </div>
  );
}

export default Card
